      <><div
      className='background-image'
      style={{
        backgroundImage: `url(${imageUrl})`
      }} /><div className='category-body-container'>
        <h2>{category.title}</h2>
        <p>Shop Now</p>
      </div></>

  );

};

export default Directory;
